package com.redstar.magic.pluginlib;import android.content.ComponentName;import android.content.Intent;import android.content.ServiceConnection;import android.os.Bundle;import android.util.Pair;import com.redstar.magic.pluginlib.container.HostActivityDelegator;/** * Created by chen.huarong on 2019-07-28. * 插件内部启动 */public interface IPluginComponentLauncher {    public static final String KEY_PLUGIN_NAME = "PLUGIN_NAME";    public static final String KEY_CLASS_NAME = "CLASS_NAME";    /**     * 启动Activity     *     * @param shadowContext 启动context     * @param intent        插件内传来的Intent.     * @return <code>true</code>表示该Intent是为了启动插件内Activity的,已经被正确消费了.     * <code>false</code>表示该Intent不是插件内的Activity.     */    boolean startActivity(ShadowContext shadowContext, Intent intent);    /**     * 启动Activity     *     * @param delegator       发起启动的activity的delegator     * @param intent          插件内传来的Intent.     * @param callingActivity 调用者     * @return <code>true</code>表示该Intent是为了启动插件内Activity的,已经被正确消费了.     * <code>false</code>表示该Intent不是插件内的Activity.     */    boolean startActivityForResult(HostActivityDelegator delegator, Intent intent,                                   int requestCode, Bundle option,                                   ComponentName callingActivity);    Pair<Boolean, ComponentName> startService(ShadowContext context, Intent service);    Pair<Boolean, Boolean> stopService(ShadowContext context, Intent name);    Pair<Boolean, Boolean> bindService(ShadowContext context, Intent service,                                       ServiceConnection conn, int flags);    Pair<Boolean, ?> unbindService(ShadowContext context, ServiceConnection conn);    /**     * 转换Intent为启动代理Activity     *     * @param pluginIntent     * @return     */    Intent convertPluginActivityIntent(Intent pluginIntent);}