package com.redstar.magic.pluginlib.components.service;import android.app.Service;import android.content.Context;import com.redstar.magic.pluginlib.proxy.service.ProxyService;/** * 插件Service的父类 * <p> * 负责将插件service的方法转交由{@link com.redstar.magic.pluginlib.proxy.service.PluginProxyService}处理 * * @author chen.huarong on 2019-11-12 */public abstract class MagicService extends Service {    private ProxyService mProxyService;    public final void setHostContextAsBase(Context context) {        attachBaseContext(context);    }    public void setProxyService(ProxyService proxyService) {        mProxyService = proxyService;    }}