package com.redstar.magic.pluginapkimport androidx.databinding.DataBindingUtilimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport com.redstar.magic.pluginapk.databinding.ActivityDatabindingBindingimport com.redstar.magic.pluginapk.viewmodel.TestViewModelimport com.redstar.magic.pluginlib.components.activity.MagicActivityclass DatabindingActivity : MagicActivity() {    var viewModel: TestViewModel = TestViewModel()    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        val view = LayoutInflater.from(this).inflate(R.layout.activity_databinding, null)        setContentView(view)        val binding = DataBindingUtil.bind<ActivityDatabindingBinding>(view)        binding?.viewModel = viewModel        findViewById<View>(R.id.button2).setOnClickListener {            viewModel.changeTextColor()        }    }}