package com.redstar.magic.pluginapkimport android.databinding.DataBindingUtilimport android.os.Bundleimport com.redstar.magic.pluginapk.databinding.ActivityDatabindingBindingimport com.redstar.magic.pluginapk.viewmodel.TestViewModelimport com.redstar.magic.pluginlib.components.activity.MagicActivityimport kotlinx.android.synthetic.main.activity_databinding.*class DatabindingActivity : MagicActivity() {    var viewModel:TestViewModel = TestViewModel()    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        val binding = DataBindingUtil.setContentView<ActivityDatabindingBinding>(this, R.layout.activity_databinding)        binding.viewModel = viewModel        button2.setOnClickListener {            viewModel.changeTextColor()        }    }}