package com.redstar.magic.pluginapk.fragmentimport android.app.Fragmentimport android.content.Contextimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport com.redstar.magic.pluginapk.Rimport com.redstar.magic.pluginapk.makeTextShort/** * A simple [Fragment] subclass. */class BlankFragment : Fragment() {    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?,                              savedInstanceState: Bundle?): View? {        // Inflate the layout for this fragment        activity?.makeTextShort("Fragment:  onCreateView")        return inflater.inflate(R.layout.fragment_blank, container, false)    }    override fun onActivityCreated(savedInstanceState: Bundle?) {        super.onActivityCreated(savedInstanceState)        activity?.makeTextShort("Fragment:  onActivityCreated")    }    override fun onAttach(context: Context?) {        super.onAttach(context)        context?.makeTextShort("Fragment:   onAttach")    }    override fun onViewCreated(view: View?, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        activity?.makeTextShort("Fragment:  onViewCreated")    }    override fun onDetach() {        super.onDetach()        activity?.makeTextShort("Fragment:  onDetach")    }    override fun onStart() {        super.onStart()        activity?.makeTextShort("Fragment:  onStart")    }    override fun onResume() {        super.onResume()        activity?.makeTextShort("Fragment:  onResume")        activity?.makeTextShort("fragment's activityName : " + activity.localClassName)    }    override fun onPause() {        super.onPause()        activity?.makeTextShort("Fragment:  onPause")    }    override fun onStop() {        super.onStop()        activity?.makeTextShort("Fragment:  onStop")    }    override fun onSaveInstanceState(outState: Bundle?) {        super.onSaveInstanceState(outState)        activity?.makeTextShort("Fragment:  onSaveInstanceState")    }    override fun onDestroyView() {        super.onDestroyView()        activity?.makeTextShort("Fragment:  onDestroyView")    }    override fun onDestroy() {        super.onDestroy()        activity?.makeTextShort("Fragment:  onDestroy")    }}