package com.redstar.magic.pluginapk.viewmodelimport androidx.databinding.ObservableIntimport android.graphics.Color/** * Created by chen.huarong on 2019-08-01. */class TestViewModel {    var textColor: ObservableInt = ObservableInt(Color.parseColor("#222222"))    fun changeTextColor() {        val color1 = Color.parseColor("#222222")        val color2 = Color.parseColor("#888888")        textColor.set(if (textColor.get() == color1) color2 else color1)    }}